                        uroles.push("STUDENT");
                      }
                      if (roles.getuserroles[0].GOVROLE_EMPLOYEE_IND == "Y") {
                        uroles.push("EMPLOYEE");
                      }
                      if (roles.getuserroles[0].GOVROLE_FACULTY_IND == "Y") {
                        uroles.push("FACULTY");
                      }
                      if (roles.getuserroles[0].GOVROLE_ALUMNI_IND == "Y") {
                        uroles.push("ALUMNI");
                      }
                      if (roles.getuserroles[0].GOVROLE_FINANCE_IND == "Y") {
                        uroles.push("FINANCE");
                      }
                      if (roles.getuserroles[0].GOVROLE_FINAID_IND == "Y") {
                        uroles.push("FINAID");
                      }
                      data.roles = uroles;
                    }
                    console.log(data);
                    authSuccess(data);
                  });
                } else {
                  authSuccess(data);
                }

              } else {
                authSuccess(data);
              }
              //authSuccess(data);
              $rootScope.getAPI(endpoint, callback);
            }).error(function(data, status, headers, config) {
              authError(data);

            });
          }
          $.unblockUI();
        });

      });

    };




    $rootScope.pspostAPI = function(endpoint, postdata, callback) {
      $rootScope.generateST($rootScope.ticket, endpoint, function(st) {
        var psUrl = "";
        if ($rootScope.demoMode) {
          psUrl = $rootScope.middlewareServerUrl + "/ps"
